mapper.select:
SELECT 

FROM park_door_control a

WHERE a.id = #{id}

SELECT 

FROM park_door_control a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.number  LIKE CONCAT('%', #{number}, '%')


		AND a.floor = #{floor}


		AND a.jiffy_stand = #{jiffyStand}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM park_door_control a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE park_door_control SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
