mapper.select:
SELECT 

FROM park_pay a

WHERE a.id = #{id}

SELECT 

FROM park_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.floor = #{floor}


		AND a.jiffyStand = #{jiffyStand}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM park_pay a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE park_pay SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
