mapper.select:
SELECT 

FROM park_space a

WHERE a.id = #{id}

SELECT 

FROM park_space a


	a.del_flag = #{DEL_FLAG_NORMAL}
	and a.isuse='1'



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM park_space a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE park_space SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
